import { Component } from '@angular/core';

@Component({
  selector: 'app-book-manage',
  templateUrl: './book-manage.component.html',
  styleUrls: ['./book-manage.component.scss']
})
export class BookManageComponent {
  public invisible: any='visible';
  public visible:any = 'd-none';

  newBook() {
    this.visible = 'visible';
    this.invisible = 'd-none'
  }
}
<app-nav ></app-nav>
<div class="d-flex flex-column align-items-center">
  <h1  class="fs-2 fw-bold text-info-emphasis">Manage Books</h1>
  <div class="d-flex justify-content-end w-100 p-5 pt-0">
    <button class="btn btn-primary" (click)="newBook()">Add BookN</button>
  </div>
  <section class="w-75">
    <app-new-member class="{{visible}} "></app-new-member>
    <app-member-list class="{{invisible}}"></app-member-list>
  </section>

</div>
